/**
 * Pega particula em uma posição x,y
 *
 */
Particula* Tecido::getParticula(int x, int y) {

	int deslocamento_linha = ( y * largura );//necessário porque vetor particula tem uma linha
	return &particulas[ deslocamento_linha + x];
}

/**
 * Constroi a restrição entre p1 e p2
 * adicionando ao vetor de restrição
 */
void Tecido::constroiRestricao(Particula *p1, Particula *p2) {

	restricoes.push_back(Restricao(p1,p2));
}

/**
 * Calcula a normal do triângulo formado por p1, p2 e p3
 */
Vector3 Tecido::calculaNormalDoTriangulo(Particula *p1,Particula *p2,Particula *p3)
{
	Vector3 pos1 = p1->getPosicao();
	Vector3 pos2 = p2->getPosicao();
	Vector3 pos3 = p3->getPosicao();

	Vector3 v1 = pos2-pos1;
	Vector3 v2 = pos3-pos1;

	return v1.produtoExterno(v2);
}

/**
 * Calcula força do vento em um triângulo formado por p1,p2,p3
 * a partir da normal.
 */
void Tecido::adicionaForcaDoVentoTriangulo(Particula *p1,Particula *p2,Particula *p3, const Vector3 direction)
{
	Vector3 normal = calculaNormalDoTriangulo(p1,p2,p3);
	Vector3 d = normal.normalizado();
	Vector3 forca = normal * (d.produtoInterno(direction));

	p1->adicionaForca(forca);
	p2->adicionaForca(forca);
	p3->adicionaForca(forca);
}

/**
 * Desenha o triângulo com os pontos p1,p2,p3 com uma cor específica
 */
void Tecido::desenhaTriangulo(Particula *p1, Particula *p2, Particula *p3, const Vector3 color)
{
	glColor3fv( (GLfloat*) &color );

	Vector3 pt1 = p1->getNormal().normalizado();
	glNormal3f(pt1.f[0], pt1.f[1], pt1.f[2]);
	glVertex3fv((GLfloat *) &(p1->getPosicao() ));

	Vector3 pt2 = p2->getNormal().normalizado();
	glNormal3f(pt2.f[0], pt2.f[1], pt2.f[2]);
	glVertex3fv((GLfloat *) &(p2->getPosicao() ));

	Vector3 pt3 = p3->getNormal().normalizado();
	glNormal3f(pt3.f[0], pt3.f[1], pt3.f[2]);
	glVertex3fv((GLfloat *) &(p3->getPosicao() ));
}

/**
 * Contrói particulas e suas restrições
 */
Tecido::Tecido(float larguraBandeira, float alturaBandeira, int largura, int altura) : largura(largura), altura(altura)
{
	particulas.resize(largura*altura);

	//Cria particulas em uma matriz, calculando as posições dela
	for(int x = 0; x < largura; x++)
	{
		for(int y = 0; y < altura; y++)
		{
			Vector3 pos = Vector3(larguraBandeira * (x/(float)largura),
							-alturaBandeira * ( (float)y/(float)altura ),
							-6);//-6 para ir para trás em relação a z
			particulas[ y*largura + x] = Particula(pos); // insere particula no vetor
		}
	}

	/**
	 * Adiciona restrições as particulas vizinhas com  distância 1
	 */
	for(int x = 0; x < largura; x++)
	{
		for(int y = 0; y < altura; y++)
		{
			if (x < largura-1) constroiRestricao(getParticula(x,y),getParticula(x+1,y));
			if (y < altura-1) constroiRestricao(getParticula(x,y),getParticula(x,y+1));
			if (x < largura-1 && y < altura-1) constroiRestricao(getParticula(x,y),getParticula(x+1,y+1));
			if (x < largura-1 && y < altura-1) constroiRestricao(getParticula(x+1,y),getParticula(x,y+1));
		}
	}

	/**
	 * Adiciona restrições as particulas vizinhas com  distância 2
	 */
	for(int x = 0; x < largura; x++)
	{
		for(int y = 0; y < altura; y++)
		{
			if (x < largura-2) constroiRestricao(getParticula(x,y),getParticula(x+2,y));
			if (y < altura-2) constroiRestricao(getParticula(x,y),getParticula(x,y+2));
			if (x < largura-2 && y < altura-2) constroiRestricao(getParticula(x,y),getParticula(x+2,y+2));
			if (x < largura-2 && y < altura-2) constroiRestricao(getParticula(x+2,y),getParticula(x,y+2));
		}
	}


	//Faz as pontas no mastro ficarem fixas
	for(int i = 0;i < 2; i++)
	{
		getParticula(0,0+i)->deslocaPosicao(Vector3(0.2,0.0,0.0));
		getParticula(0 ,0+i)->fixar();

		getParticula(0,altura-1-i)->deslocaPosicao(Vector3(0.2,0.0,0.0));
		getParticula(0,altura-1-i)->fixar();
	}
}


/**
 * Retorna a cor com base na matriz 'coresBandeira'
 */
Vector3 Tecido::getCor(int indice) {

	if (indice == 1 || indice == 5)
	{
		return Vector3(0, 35.0f/255, 149.0f/255);
	}
	else if (indice == 2 || indice == 3 || indice == 4) {
		return Vector3(1, 1, 1);
	}
	else
	{
		return Vector3(1,0,0);
	}
}

/**
 * Desenha o tecido como uma malha de triângulos.
 * Usamos uma matriz (gigante) para além de dizer qual a cor de cada triângulo,
 * tratarmos direção das diagonais, para conseguir desenhar os triângulos que formam
 * a estrela da bandeira de Israel por exemplo.
 *
 * Este é o corte padrão:
 * (x,y)   *--* (x+1,y)
 *         | /|
 *         |/ |
 * (x,y+1) *--* (x+1,y+1)
 *
 * Mas também temos:
 * (x,y)   *--* (x+1,y)
 *         |\ |
 *         | \|
 * (x,y+1) *--* (x+1,y+1)
 *
 */
void Tecido::desenha()
{
	// reseta todas as normais das partículas
	std::vector<Particula>::iterator particula;
	for(particula = particulas.begin(); particula != particulas.end(); particula++)
	{
		(*particula).resetaNormal();
	}

	// cria normais para cada partícula, adicionando todas as normais dos triângulos que ela faz parte
	for(int x = 0; x < largura-1; x++)
	{
		for(int y = 0; y < altura-1; y++)
		{
			Vector3 normal = calculaNormalDoTriangulo(getParticula(x+1,y),getParticula(x,y),getParticula(x,y+1));
			getParticula(x+1,y)->adicionaNormal(normal);
			getParticula(x,y)->adicionaNormal(normal);
			getParticula(x,y+1)->adicionaNormal(normal);

			normal = calculaNormalDoTriangulo(getParticula(x+1,y+1),getParticula(x+1,y),getParticula(x,y+1));
			
			getParticula(x+1,y+1)->adicionaNormal(normal);
			getParticula(x+1,y)->adicionaNormal(normal);
			getParticula(x,y+1)->adicionaNormal(normal);
		}
	}

	glShadeModel(GL_SMOOTH);
	glBegin(GL_TRIANGLES);

	for(int x = 0; x < largura-1; x++)
	{
		for(int y = 0; y < altura-1; y++)
		{
			Vector3 color(0,0,0);
			color = getCor(coresBandeira[y][x*2]);

			int x1, x2, x3, x4, x5, x6;
			int y1, y2, y3, y4, y5, y6;

			// se for 4 ou 5, inverte o corte do quadrado
			if (coresBandeira[y][x*2] == 4 || coresBandeira[y][x*2+1] == 4 ||
				coresBandeira[y][x*2] == 5 || coresBandeira[y][x*2+1] == 5) {
				x1 = x;
				x2 = x;
				x3 = x+1;
				y1 = y;
				y2 = y+1;
				y3 = y + 1;

				x4 = x+1;
				x5 = x+1;
				x6 = x;
				y4 = y + 1;
				y5 = y;
				y6 = y;
			} else {
				x1 = x + 1;
				x2 = x;
				x3 = x;
				y1 = y;
				y2 = y;
				y3 = y + 1;

				x4 = x+1;
				x5 = x+1;
				x6 = x;
				y4 = y + 1;
				y5 = y;
				y6 = y + 1;
			}

			desenhaTriangulo(getParticula(x1,y1),getParticula(x2,y2),getParticula(x3,y3),color);

			color = getCor(coresBandeira[y][x*2+1]);


			desenhaTriangulo(getParticula(x4,y4),getParticula(x5,y5),getParticula(x6,y6),color);
		}
	}

	glEnd();
}

/**
 * Método importante onde o tempo progrediu um pouco em todo o tecido.
 * Chama a satisfazerRestricao e proximoFrame para todas as particulas,
 * a fim de ajustar o tecido e calcular as posições conforme as forças
 */
void Tecido::proximoFrame()
{
	std::vector<Restricao>::iterator restricao;

	// itera sobre todas as restrições várias vezes
	for(int i = 0; i < ITERACOES_DE_RESTRICOES; i++)
	{
		for(restricao = restricoes.begin(); restricao != restricoes.end(); restricao++ )
		{
			(*restricao).satisfazerRestricao(); // restrição satisfeita.
		}
	}

	std::vector<Particula>::iterator particula;

	for(particula = particulas.begin(); particula != particulas.end(); particula++)
	{
		(*particula).proximoFrame(); // calcula posição para cada particula do próximo frame
	}
}

/**
 * Adiciona gravidade para todas as particulas
 */
void Tecido::adicionaForca(const Vector3 direcao)
{
	std::vector<Particula>::iterator particula;

	for(particula = particulas.begin(); particula != particulas.end(); particula++)
	{
		(*particula).adicionaForca(direcao); // adiciona força para cada particula
	}
}

/**
 * Adiciona vento para todas as particulas, já que a força final é proporcional a área do triângulo
 */
void Tecido::vento(const Vector3 direcao)
{
	for(int x = 0; x < largura-1; x++)
	{
		for(int y = 0; y < altura-1; y++)
		{
			adicionaForcaDoVentoTriangulo(getParticula(x+1,y),getParticula(x,y),getParticula(x,y+1),direcao);
			adicionaForcaDoVentoTriangulo(getParticula(x+1,y+1),getParticula(x+1,y),getParticula(x,y+1),direcao);
		}
	}
}
